import sys, os, io
input = io.BytesIO(os.read(0, os.fstat(0).st_size)).readline

def f(u, v):
    return u * m + v

def g(u):
    return u // m, u % m

def bfs():
    dist = [inf] * l
    q, q0 = [], []
    k, k0 = 0, 0
    for i in range(0, l, m):
        if not ng[i]:
            dist[i] = s[i]
            if not dist[i]:
                q.append(i)
            else:
                q0.append(i)
    parent = [-1] * l
    for d in range(l + 1):
        while len(q) ^ k:
            u = q[k]
            i, j = g(u)
            for di, dj in v2:
                ni, nj = i + di, j + dj
                if not 0 <= ni < n or not 0 <= nj < m:
                    continue
                v = f(ni, nj)
                if ng[v] or dist[v] ^ inf:
                    continue
                dist[v] = d + s[v]
                parent[v] = u
                if not s[v]:
                    q.append(v)
                else:
                    q0.append(v)
            k += 1
        q, q0 = q0, q
        k, k0 = k0, k
    return dist, parent

t = int(input())
ans = []
v = [(1, 0), (0, 1), (-1, 0), (0, -1)]
v2 = [(1, 1), (1, -1), (-1, 1), (-1, -1)]
inf = pow(10, 9) + 1
for _ in range(t):
    n, m = map(int, input().split())
    l = n * m
    s = []
    ng = [0] * l
    for i in range(n):
        s0 = list(input().rstrip())
        for j in range(m):
            if s0[j] & 1:
                for di, dj in v:
                    ni, nj = i + di, j + dj
                    if not 0 <= ni < n or not 0 <= nj < m:
                        continue
                    ng[f(ni, nj)] = 1
        for j in s0:
            s.append((j & 1) ^ 1)
    dist, parent = bfs()
    mi = inf
    for i in range(m - 1, l, m):
        if mi > dist[i]:
            mi, u = dist[i], i
    if mi == inf:
        ans0 = "NO"
        ans.append(ans0)
        continue
    s[u] = 0
    while not parent[u] == -1:
        u = parent[u]
        s[u] = 0
    ans0 = "YES"
    ans.append(ans0)
    for i in range(n):
        ans0 = ["." if s[j] else "#" for j in range(i * m, (i + 1) * m)]
        ans.append("".join(ans0))
sys.stdout.write("\n".join(ans))
