t = int(input())

for tt in range(t):
    s=input()
    l=set()
    #l.append(s[0])
    n=len(s)
    if n==1:
        print(s,end="")
    else:
        left = 0
        right = 1
        while left<n and right<n:
            if s[left]==s[right]:
                left+=2
                right+=2
            else:
                l.add(s[left])
                left+=1
                right+=1
        if left==n-1:
            l.add(s[left])
    li = list(l)    
    li.sort()
    for i in li:
        print(i,end="")
    print("")
