from math import gcd
tt = int(input())
for t in range(tt):
    a,b = map(int, input().split())
    MAX = max(a,b)
    MIN = min(a,b)
    if gcd(a,b) == MAX-MIN:
        print(MAX-MIN,0)
    elif a==b:
        print(0,0)
    else:
        d = MAX-MIN
        if d>0:
            f = MAX-(MAX%d)
        s = f+d
        print(d,min(abs(MAX-f),abs(MAX-s)))
