h=10**9
for _ in range(int(input())):
	n=int(input());a=list(map(int,input().split()));a.sort();
	def f(t):
		c=a[0]*a[-1]+t*(a[0]+a[-1])
		for i in range(1,n-1):c+=min(a[0]*a[i]+t*(a[0]+a[i]),a[-1]*a[i]+t*(a[-1]+a[i]))
		return c
	if f(h)>0 or f(-h)>0:print("INF");
	else:
		l,r=-h,h
		while r-l>3:
			p,q=(l*2+r)//3,(l+2*r)//3;
			if f(p)>f(q):r=q;
			else:l=p;
		z=-h*h
		for i in range(l,r):z=max(z,f(i))
		print(z)
