from sys import stdin, gettrace

if gettrace():
    def inputi():
        return input()
else:
    def input():
        return next(stdin)[:-1]


    def inputi():
        return stdin.buffer.readline()


def solve():
    n = int(input())
    cc = [[int(c) for c in input().split()] for _ in range(2*n)]
    res = sum(sum(cc[i][n:]) for i in range(n, 2*n)) + min(cc[0][n],
                                                           cc[0][2*n - 1],
                                                           cc[n-1][n],
                                                           cc[n-1][2*n -1],
                                                           cc[n][0],
                                                           cc[2*n-1][0],
                                                           cc[n][n-1],
                                                           cc[2*n-1][n-1])
    print(res)

def main():
    t = int(input())
    for _ in range(t):
        solve()


if __name__ == "__main__":
    main()
