from itertools import accumulate
from math import log
import sys
input = sys.stdin.buffer.readline

def parent(i):
	return i//2

def left_c(i):
	return i*2

def right_c(i):
	return i*2+1

def fill_tree(i, fro, to):
	if fro == to:
		tr[i] = pf[fro]
		d[fro] = i
		di[i] = fro
	else:
		tr[i] = min(fill_tree(left_c(i), fro, (fro+to)//2), fill_tree(right_c(i),(fro+to)//2 + 1,to))
	return tr[i]

def f(i):
	def ascend(i):
		if i == 1:
			return 1
		par = parent(i)
		cand = right_c(par)
		if tr[cand] < val:
			return cand
		return ascend(par)

	def descend(i):
		lc, rc = left_c(i), right_c(i)
		# if sum([tr[rc] is None, tr[lc] is None]) == 1:
		# 	assert False
		if rc <= max_i and tr[rc] is not None and tr[lc] is not None:
			# har två barn
			if tr[lc] < val:
				return descend(lc)
			else:
				return descend(rc)
		else:
			# har inga barn
			if tr[i] < val:
				return di[i]-1
			else:
				return di[i]





	ind = d[i]
	sta_pf = pf[i]
	val = sta_pf-s
	sto = descend(ascend(ind))
	# print("ggg", i, sto)
	return sto - i


for _ in range(int(input())):
	n, s = map(int, input().split())
	a = map(int, input().split())
	pf = [0]+list(accumulate(a))
	tr = [None] * (4 * n)
	d = {}
	di = {}
	fill_tree(1, 0, n)
	ans = 0
	sta = -1
	max_i = -1
	# print("pf",pf)
	for i in range(len(tr)):
		if tr[i] is not None:
			max_i = i
	# print("max_i",max_i)
	# for i in range(max_i):
	# 	if tr[i] is None:
	# 		print(tr[i],i)
	for i in range(n):
		ans1 = f(i)
		if ans1 > ans:
			ans = ans1
			sta = i
	if ans == 0:
		print(-1)
	else:
		print(sta+1, sta+ans)
