for i in range(int(input())):
    a=list(map(int,input().split(' ')))
    if a[0]==a[1]:
        if a[0]!=1:
            print('YES')
        else:
            print('NO')
    else:
        k=a[2]
        if a[0]%2!=0:
            d=1+(a[1]-a[0])//2
        else:
            d=(a[1]-a[0]+1)//2
        if k>=d:
            print('YES')
        else:
            print('NO')
