import sys
import math
import heapq
import itertools
import bisect
import random
import time
from collections import deque
input=sys.stdin.readline

mod=10**9+7
def power(a,n):
    if n==0:
        return 1
    x=power(a,n//2)
    if n%2==0:
        return x*x%mod
    else:
        return x*x*a%mod

def d(a):
    return power(a,mod-2)

d2=d(2)
pp=[d(power(2,0))]
for i in range(1000005):
    pp.append((pp[-1]*d2)%mod)
t=int(input())
for _ in range(t):
    n=int(input())

    ans=[1 for _ in range(n+1)]
    dp=[0 for _ in range(n+1)]
    for i in range(n//2+1,n+1):
        dp[i]=1

    dp[n//2]=(1-d(pow(2,n//2-1)))%mod
    
    for i in range(n//2-1,0,-1):
        dp[i]=(dp[i+1]-dp[i*2]*pp[i]-dp[i*2+1]*pp[i+1])%mod
    
    for i in range(1,n+1):
        p=max(0,(i-1)//2)
   
        ans[i]=(dp[i]*pp[p])%mod
        if i!=n:
            ans[i]=(ans[i]*d2)%mod

    for i in range(1,n+1):
        print(ans[i])
