import sys,math
input=sys.stdin.readline
for  _ in range(int(input())):
    #n=int(input())
    l,r=map(int,input().split())
    #a=list(map(int,input().split()))
    a=math.isqrt(l)
    b=math.isqrt(r)
    ans=0
    if a==b:
        if r>=a**2>=l:
            ans+=1  
        if r>=(a**2)+2*a>=l:
            ans+=1
        if r>=(a**2)+a>=l:
            ans+=1
    else:
        if a+1<=b-1:
            ans+=3*(b-1-a)
        if a**2>=l:
            ans+=1  
        if (a**2)+2*a>=l:
            ans+=1
        if (a**2)+a>=l:
            ans+=1
        if b**2<=r:
            ans+=1  
        if (b**2)+2*b<=r:
            ans+=1
        if (b**2)+b<=r:
            ans+=1
    print(ans)
