import sys 
input=sys.stdin.readline
for iii in range(int(input())):
        n,m=map(int,input().split())
        q=list(map(int,input().split()))
        q1=[0 for i in range(18)]
        for i in q:
            x=bin(i)
            x=x[2:]
            x=x[::-1]
            for j in range(len(x)):
                if x[j]=='1':
                    q1[j]+=1
     
        q2=[0 for i in range(18)]
        for i in range(n,m+1,1):
            x = bin(i)
            x = x[2:]
            x = x[::-1]
            for j in range(len(x)):
                if x[j] == '1':
                    q2[j] += 1
     
        summ=0
        for i in range(18):
            if q1[i]!=q2[i]:
                summ=summ+2**i
        print(summ)
