import sys
input = sys.stdin.readline
dd = [0] * (2 ** 17 + 1)
for _ in range(int(input())):
    l, r = [int(xx) for xx in input().split()]
    a = [int(xx) for xx in input().split()]
    for i in range(r - l + 1):
        dd[a[i]] = 1
    if l % 2 == 0 and r % 2 == 1:
        if r - l > 1000:
            g = [0] * 17
            for i in range(1, 17):
                k = 0
                for j in range(r - l + 1):
                    if a[j] % (2 ** (i + 1)) >= 2 ** i:
                        k += 1
                l1 = 0
                for j in range(l, r + 1):
                    if j % (2 ** (i + 1)) >= 2 ** i:
                        l1 += 1
                if l1 != k:
                    g[i] = 1
            ans = 0
            for i in range(1, 17):
                ans += g[i] * (2 ** i)
            print(int(ans))
        else:
            for i in range(r - l + 1):
                x = a[i] ^ l
                for j in range(l, r + 1):
                    if not dd[j ^ x]:
                        break
                else:
                    print(x)
                    break
    else:
        h = []
        for i in range(r - l + 1):
            if not dd[a[i] ^ 1]:
                h.append(a[i])
        for i in range(len(h)):
            x = h[i] ^ l
            for j in range(r - l + 1):
                if a[j] ^ x < l or a[j] ^ x > r:
                    break
            else:
                print(x)
                break
        else:
            for i in range(len(h)):
                x = h[i] ^ r
                for j in range(r - l + 1):
                    if a[j] ^ x < l or a[j] ^ x > r:
                        break
                else:
                    print(x)
                    break
    for i in range(r - l + 1):
        dd[a[i]] = 0
