from collections import deque

def connected(n, arr):
    g = [[] for i in range(n)]
    for k in range(30):
        ind = []
        for i, a in enumerate(arr):
            if a&(1<<k):
                ind.append(i)
        if len(ind) > 1:
            a = ind.pop()
            for i in ind:
                g[i].append(a)
                g[a].append(i)
    v = [0]*n
    v[0] = 1
    q = deque()
    q.append(0)
    while q:
        x = q.popleft()
        for y in g[x]:
            if v[y] == 0:
                v[y] = 1
                q.append(y)
    return 0 not in v


def solve():
    n = int(input())
    arr = list(map(int, input().split()))
    ans = 0
    for i, a in enumerate(arr):
        if a == 0:
            ans += 1
            arr[i] = 1
    if connected(n, arr):
        print(ans)
        print(*arr)
        return
    for i in range(n):
        for j in (-1, 1):
            arr1 = arr[:]
            arr1[i] += j
            if connected(n, arr1):
                print(ans+1)
                print(*arr1)
                return
    m = max(a&-a for a in arr)
    L = []
    for i, a in enumerate(arr):
        if a&-a == m:
            L.append(i)
    arr[L[0]] -= 1
    arr[L[1]] += 1
    print(ans+2)
    print(*arr)


import sys
input = lambda: sys.stdin.readline().rstrip()
t = int(input())
for i in range(t):
    solve()
