import io,sys,os,math
input = io.BytesIO(os.read(0,os.fstat(0).st_size)).readline

def printArr(arr):
	sys.stdout.write(" ".join(map(str,arr)) + "\n")



def solve(stations,extraTime,n):
	answer=stations[0][0]  + extraTime[0]
	if n==1:
		return answer
	answer += abs(math.ceil((stations[0][1] - stations[0][0])/2))
	if not answer>=stations[0][1]:
			answer= stations[0][1]
	for i in range(1,n):
		answer+= stations[i][0]  + extraTime[i] - stations[i-1][1]
		if i<n-1:
			answer += abs(math.ceil((stations[i][1] - stations[i][0])/2))
			if not answer>=stations[i][1]:
				answer=stations[i][1]


	return answer

t=int(input().decode())
while t>0:
	n=int(input().decode())
	stations = list()
	for i in range(n):
		arr=[int(i) for i in input().decode().strip().split()]
		stations.append(arr)
	extraTime = [int(i) for i in input().decode().strip().split()]
	answer=solve(stations,extraTime,n)
	sys.stdout.write(str(answer)+'\n')
	t-=1
