from collections import defaultdict
import sys
input = sys.stdin.readline

k = int(input())
inf = pow(2, k) - 1
mod = pow(10, 9) + 7
d = {"white":0, "green":1, "red":2, "orange":3, "blue":4, "yellow":5}
n = int(input())
x = []
se = set()
for _ in range(n):
    v, s = input().rstrip().split()
    x.append((int(v), d[s]))
    se.add(int(v))
ok = defaultdict(lambda : [1] * 6)
for v, j in x:
    for i in [v // 2, 2 * v, 2 * v + 1]:
        if not i in se and 1 <= i <= inf:
            ok[i][j], ok[i][5 - j] = 0, 0
mi = defaultdict(lambda : set())
for u, _ in x:
    v = u
    while v > 1 and not v // 2 in se:
        v //= 2
    w = u
    while w > 1 and not w // 2 in se:
        w //= 2
        mi[v].add(w)
ans = 1
for u, j in x:
    for v, k in x:
        if u // 2 == v and (not j ^ k or not (j + k) % 5):
            ans = 0
    if not ans:
        break
c = 0
for i in mi.keys():
    if not ans:
        break
    mi0 = mi[i]
    c += len(mi0)
    p = [i]
    child = [[] for _ in range(len(mi0))]
    j = 0
    while len(p) ^ j:
        u = 2 * p[j]
        v = u + 1
        f = 0
        if u in mi0:
            p.append(u)
            child[j].append(len(p) - 1)
            f = 1
        if v in mi0:
            p.append(v)
            child[j].append(len(p) - 1)
            f = 1
        j += 1
    dp = [[0] * 6 for _ in range(len(mi0))]
    while len(p):
        u = p.pop()
        j = len(p)
        cj = child[j]
        dpj = dp[j]
        oku = ok[u]
        if not len(cj):
            for k in range(6):
                dpj[k] = oku[k]
        elif len(cj) == 1:
            dp1 = dp[cj[0]]
            for k in range(6):
                if not oku[k]:
                    continue
                for l in range(6):
                    if not k ^ l or not (k + l) % 5:
                        continue
                    dpj[k] += dp1[l]
                    dpj[k] %= mod
        else:
            dp1, dp2 = dp[cj[0]], dp[cj[1]]
            for k in range(6):
                if not oku[k]:
                    continue
                for l in range(6):
                    if not k ^ l or not (k + l) % 5:
                        continue
                    for m in range(6):
                        if not k ^ m or not (k + m) % 5:
                            continue
                        dpj[k] += dp1[l] * dp2[m] % mod
                        dpj[k] %= mod
    ans *= sum(dp[0]) % mod
    ans %= mod
ans *= pow(4, inf - len(se) - c, mod)
ans %= mod
print(ans)
