def win(a, b):
    cnt = 0
    for i in range(5):
        if a[i] < b[i]:
            cnt += 1
    if cnt >= 3:
        return a
    return b


for _ in range(int(input())):
    n = int(input())
    l = []
    for i in range(n):
        l.append(list(map(int, input().split())))
    ini = l[0]
    idx = 0
    for i in range(1, n):
        if ini != win(ini, l[i]):
            idx = i
            ini = l[i]
    f = 1
    for i in range(n):
        if ini != win(ini, l[i]):
            f = 0
    for i in range(n):
        if ini != win(l[i], ini):
            f = 0
    if not f:
        print(-1)
    else:
        print(idx+1)
