import sys

t = int(sys.stdin.readline())
for _ in range(t):
    n = int(sys.stdin.readline())
    s = sys.stdin.readline().strip().lower()
    a = sys.stdin.readline().strip().lower().split()
    k = int(a[0])

    d = [0]*256
    for i in range(1, k+1):
        d[ord(a[i])] = 1

    parts = 0
    maxlen = 0
    curlen = 0
    grps = []
    for i in range(len(s)):
        v = d[ord(s[i])]
        if i == 0:
            v = 0
        if v > 0:
            if curlen > 0:
                parts += 1
            if curlen > maxlen:
                maxlen = curlen
            grps.append(curlen)
            curlen = 0
        else:
            curlen += 1

    # print(grps)
    cnt = 0
    for i in range(len(grps)):
        if i > 0:
            if grps[i] == maxlen:
                cnt += 1

    res = 0
    if parts == 1:
        res = max(maxlen,1)
    elif parts > 1:
        if cnt >= 1:
            res = maxlen + 1
        else:
            res = max(maxlen,1)

    print(res)
