import heapq
import sys, os, io
input = io.BytesIO(os.read(0, os.fstat(0).st_size)).readline
 
def f(u, v):
    return u * m + v
 
def dijkstra():
    dist = [inf] * l
    visit = [0] * l
    h = []
    for i in range(n):
        si = s[i]
        for j in range(m):
            if si[j] == 46:
                u = f(i, j)
                dist[u] = 0
                heapq.heappush(h, (dist[u], u))
    while h:
        d, u = heapq.heappop(h)
        if dist[u] < d:
            continue
        visit[u] = 1
        for v in G[u]:
            c = q if u // m == v // m or not (u - v) % m else p
            nd = dist[u] + c
            if not visit[v] and nd < dist[v]:
                dist[v] = nd
                heapq.heappush(h, (dist[v], v))
    return dist
 
n, m = map(int, input().split())
p, q = map(int, input().split())
s = [list(input().rstrip()) for _ in range(n)]
l = n * m
G = [[] for _ in range(l)]
v = [(1, 0), (-1, 0), (0, 1), (0, -1)]
for i in range(n):
    si = s[i]
    for j in range(m):
        if si[j] == 76:
            x, y = f(i, j), f(i, j + 1)
            for k in range(2):
                for di, dj in v:
                    ni, nj = i + di, j + k + dj
                    if not 0 <= ni < n or not 0 <= nj < m:
                        continue
                    z = f(ni, nj)
                    if x == z or y == z or s[ni][nj] == 35:
                        continue
                    if not k:
                        G[z].append(y)
                    else:
                        G[z].append(x)
        elif si[j] == 85:
            x, y = f(i, j), f(i + 1, j)
            for k in range(2):
                for di, dj in v:
                    ni, nj = i + k + di, j + dj
                    if not 0 <= ni < n or not 0 <= nj < m:
                        continue
                    z = f(ni, nj)
                    if x == z or y == z or s[ni][nj] == 35:
                        continue
                    if not k:
                        G[z].append(y)
                    else:
                        G[z].append(x)
inf = pow(10, 15) + 1
dist = dijkstra()
ans = inf
for i in range(n):
    for j in range(m - 1):
        ans = min(ans, dist[f(i, j)] + dist[f(i, j + 1)])
for i in range(n - 1):
    for j in range(m):
        ans = min(ans, dist[f(i, j)] + dist[f(i + 1, j)])
ans = (ans + 1) % (inf + 1) - 1
print(ans)
