t = int(input())

def solve(n, a):
    x, y = 0, 0
    for i in a:
        if (i&1): x = x+1
        else: y = y+1

    def f(x):
        return (((x>>1)+(x&1))&1)

    if x>=2:
        if not f(x) and not (x&1):
            return "Alice"
        if f(x) and not (x&1):
            return "Bob"
        if not f(x) or (y&1):
            return "Alice"
        return "Bob"
    
    else:
        if x==0:
            return "Alice"
        if x==1 and (y&1):
            return "Alice"
        return "Bob"
        

for i in range(t):
    n = int(input())
    a = list(map(int, input().split()))
    print(solve(n, a))
