import sys
from math import *
def input(): return sys.stdin.readline().strip()


def lcm(a, b):
    return a*b//gcd(a, b)


for test in range(int(input())):
    n = int(input())
    arr = list(map(int, input().split()))
    if n <= 2:
        print("YES")
        continue
    brr = [arr[0]]
    for i in range(n-1):
        brr.append(lcm(arr[i], arr[i+1]))
    brr.append(arr[-1])
    crr = []
    for i in range(n):
        crr.append(gcd(brr[i], brr[i+1]))
    if arr == crr:
        print("YES")
    else:
        print("NO")
