import sys

input = sys.stdin.readline
inf = float('inf')
xrange = range


def getInt():
    return int(input())


def getStr():
    return input().strip()


def getList(split=True):
    s = getStr()
    if split:
        s = s.split()
    return map(int, s)


# t = getInt()
t = 1
M = 998244353
# N = 100


def comb(n, k):
    if k > n or min(n, k) < 0:
        return 0
    res = 1
    for i in range(k):
        res *= (n-i)
        res *= pow(i+1, M-2, M)
        res %= M
    return res


def solve():
    n, k = getList()
    sa = list(getList())
    rank = [0] * n
    for i, j in enumerate(sa):
        rank[j] = i
    cnt = 0
    for i in range(n-1):
        if sa[i] + 1 < n and (sa[i+1] == n - 1 or rank[sa[i]+1] > rank[sa[i+1]+1]):
            cnt += 1
    # add an additional slot to ensure that it is < k
    # hence the first element must be >= 1
    # distribute (k-1-cnt) among (n+1) boxes
    print(comb(k-cnt+n-1, k-1-cnt))


for _ in range(t):
    solve()
