import math
from sys import stdin
input = stdin.readline
#// - remember to add .strip() when input is a string

t = int(input())

for _ in range(t):

  n = int(input())

  arr = list(map(int,input().split()))

  new = []

  temp = []

  for i in range(len(arr)):

    if arr[i] == 0:

      if len(temp) > 0:
        new.append(temp)
        temp = []

    else:

      temp.append(arr[i])

    if i == len(arr) - 1:

      if len(temp) > 0:
        new.append(temp)

 
  maxans = 0
  lensub = 0
  for i in new:


    first_neg = -1
    last_neg = -1
    pref_prod = 0
    suff_prod = 0
    prod = 0
    count_neg = 0
    for j in range(len(i)):
      if abs(i[j]) == 2:
        prod += 1
        suff_prod += 1
        
      if first_neg == -1:
        if abs(i[j]) == 2:
          pref_prod += 1
        
        
      if i[j] < 0:
        last_neg = j
        count_neg += 1
        if first_neg == -1:
          first_neg = j
        suff_prod = 0
        if i[j] == -2:
          suff_prod = 1

    if count_neg % 2 == 0:

      if prod > maxans:
        maxans = prod
        lensub = len(i)
    #elif count_neg == 1:
      #pass
    else:

      if pref_prod > suff_prod:
        prod = prod - suff_prod
        if prod > maxans:
          maxans = prod
          lensub = last_neg
      else:
        prod = prod - pref_prod
        if prod > maxans:
          maxans = prod
          lensub = len(i) - first_neg-1

  #print("GSaWAaa",maxans, lensub)
  zero = 0
  prod = 0
  negs = 0
  for i in range(lensub):
    if arr[i] < 0:
      negs += 1
    if arr[i] == 0:
      
      zero += 1
    if abs(arr[i]) == 2:
      prod += 1
  
  if prod == maxans and zero == 0 and negs % 2 == 0:
    if lensub == 0:
      print(0, len(arr))
    else:
      print(0, len(arr)-(lensub-1)-1)
  else:

    for i in range(lensub,len(arr)):
      
      if arr[i-lensub] == 0:
        zero -= 1
      if arr[i-lensub] < 0:
        negs -= 1
      if abs(arr[i-lensub]) == 2:
        prod -= 1
      
      
      if arr[i] == 0:
        zero += 1
      if arr[i] < 0:
        negs += 1
      if abs(arr[i]) == 2:
        prod += 1
      if prod == maxans and zero == 0 and negs % 2 == 0:
        if lensub == 0:
          print(0, len(arr))
        else:
          print(i-lensub+1, len(arr)-i-1)
        break
