import random
import sys
input = sys.stdin.readline

class DSU:
    def __init__(self, n):
        self.p = [i for i in range(n)]
        self.size = [1]*n

    def find(self, x):
        root = x
        while root != self.p[root]:
            root = self.p[root]
        while x != self.p[x]:
            tmp = x
            self.p[tmp] = root
            x = self.p[x]
        return root

    def union(self, x, y):
        if self.size[x] < self.size[y]:
            x, y = y, x
        pa, pb = self.find(x), self.find(y)
        if pa == pb:
            return False
        self.p[pb] = pa
        self.size[pa] += self.size[pb]
        return True

def solve():
    n, m = map(int, input().split())
    dsu = DSU(n+1)
    re, be = [], []
    rs = set()
    ans = ["0"]*m
    for i in range(m):
        u, v = map(int, input().split())
        if dsu.union(u, v):
            be.append((i, u, v))
        else:
            re.append((i, u, v))
            rs.add(u)
            rs.add(v)
            ans[i] = "1"

    if len(rs) == 3:
        idx, i, j = re[0]
        ans[idx] = "0"
        be.append((idx, i, j))
        m = len(be)
        dsu = DSU(n+1)
        for i in range(m-1, -1, -1):
            idx, u, v = be[i]
            if dsu.union(u, v):
                continue
            ans[idx] = "1"
    return "".join(ans)




for _ in range(int(input())):
    print(solve())
