def lcm(a,b):
    if a<b:
        sm=a
    else:
        sm=b
    for i in range(1,sm+1):
        if a%i==0 and b%i==0:
            hcf=i
    return int(a*b/hcf)
T=int(input())
for i in range(T):
    t=str(input())
    s=str(input())
    p=s
    q=t
    l1=int(lcm(int(len(s)),int(len(t)))/int(len(s)))
    l2=int(lcm(int(len(s)),int(len(t)))/int(len(t)))
    for j in range(l1-1):
        s+=p
    for k in range(l2-1):
        t+=q
    if (s==t):
        print(s,"\n")
    else:
        print("-1\n")
