import sys
input = sys.stdin.readline

for _ in range(int(input())):
    n,m=list(map(int,input().split()))
    arr=[]
    for _ in range(n):
        arr.append(list(map(int,input().split())))
    d1=[0]*(n+m-1)
    d2=[0]*(n+m-1)
    for i in range(n):
        for j in range(m):
            d1[i+j]+=arr[i][j]
            d2[i-j+m-1]+=arr[i][j]
    maxx=0
    for i in range(n):
        for j in range(m):
            maxx=max(maxx, d1[i+j]+d2[i-j+m-1]-arr[i][j])
    print(maxx)
