import sys

# store inf in a variable called inf
inf = float('inf')


# change stdout buffer size
buffer = open(1, 'w', 10**6)


# fast printing function
def print(*args, sep=' ', end='\n'):
    buffer.write(sep.join(str(arg) for arg in args) + end)


# flush stdout
def flush():
    buffer.flush()


# fast input
def input():
    return sys.stdin.readline()


# read a list of integers from stdin
def read_ints(index=None):
    return [int(x) for x in input().split()]


def read_ints_indexed(index):
    return [(int(x), i + index) for i, x in enumerate(input().split())]


def solve():
    n, m = read_ints()
    board = []
    for i in range(n):
        board.append([x == 'B' for x in input()])
        board[i].append(False)
    board.append([False] * (m + 1))

    a = [[False] * m for _ in range(n)]

    for i in range(n):
        for j in range(m):
            a[i][j] = board[i][j] ^ board[i][j+1] ^ board[i+1][j] ^ board[i+1][j+1]

    count = 0
    operation4 = 0
    for i in range(n):
        for j in range(m):
            count += a[i][j]
            if i < n - 1 and j < m - 1 and a[i][j] and a[i][m - 1] and a[n-1][j] and a[n-1][m-1]:
                operation4 = 1

    print(count - operation4)


if __name__ == '__main__':
    solve()
