t = int(input())
for case in range(t):
    n, m = map(int, input().split())
    s = input()
    x, y = 1, 1
    x_max_r, x_max_l, y_max_u, y_max_d = 0, 0, 0, 0
    x_last, y_last = 1, 1
    x_mov, y_mov = 0, 0

    # First find longest path
    for i in s:
        if i == "R":
            x_mov += 1
        elif i == "L":
            x_mov -= 1
        elif i == "D":
            y_mov += 1
        elif i == "U":
            y_mov -= 1

        if x_max_r < x_mov:
            x_max_r = x_mov
        elif x_max_l > x_mov:
            x_max_l = x_mov
        if y_max_d < y_mov:
            y_max_d = y_mov
        elif y_max_u > y_mov:
            y_max_u = y_mov


        if x + x_mov < 1 and x+1 <= m:
            x += 1
        elif x + x_mov > m and x-1 > 0:
            x -= 1
        if y + y_mov < 1 and y+1 <= n:
            y += 1
        elif y + y_mov > n and y-1 > 0:
            y -= 1

        if x + x_max_r > m or x + x_max_l < 1:
            x = x_last
            break
        elif y + y_max_d > n or y + y_max_u < 1:
            y = y_last
            break
        else:
            x_last, y_last = x, y
    print(y, x)

