import os
import sys
from io import BytesIO, IOBase

## PYRIVAL BOOTSTRAP
# https://github.com/cheran-senthil/PyRival/blob/master/pyrival/misc/bootstrap.py
# This decorator allows for recursion without actually doing recursion
from types import GeneratorType
## @bootstrap, yield when getting and returning value in recursive functions


def main():
    @bootstrap
    def dfs(value):
        for v in beaten_by[value]:
            if iptw[v] == '1':
                continue
            else:
                iptw[v] = '1'
                yield dfs(v)
        yield

    for _ in range(iip()):
        n = iip()
        a = liip()
        b = liip()

        beaten_by = [set() for _ in range(n)]
        li = []
        for i in range(n):
            li.append((a[i], b[i], i))

        starts = set()

        li.sort(key=lambda x: -x[0])
        starts.add(li[0][2])
        for i in range(n - 1):
            beaten_by[li[i + 1][2]].add(li[i][2])

        li.sort(key=lambda x: -x[1])
        starts.add(li[0][2])
        for i in range(n - 1):
            beaten_by[li[i + 1][2]].add(li[i][2])

        iptw = ['0'] * n # is possible to win
        for v in starts:
            iptw[v] = '1'

        for start in starts:
            dfs(start)

        print(''.join(iptw))

    
# region fastio
 
BUFSIZE = 8192
 

class FastIO(IOBase):
    newlines = 0
 
    def __init__(self, file):
        self._fd = file.fileno()
        self.buffer = BytesIO()
        self.writable = "x" in file.mode or "r" not in file.mode
        self.write = self.buffer.write if self.writable else None
 
    def read(self):
        while True:
            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))
            if not b:
                break
            ptr = self.buffer.tell()
            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)
        self.newlines = 0
        return self.buffer.read()
 
    def readline(self):
        while self.newlines == 0:
            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))
            self.newlines = b.count(b"\n") + (not b)
            ptr = self.buffer.tell()
            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)
        self.newlines -= 1
        return self.buffer.readline()
 
    def flush(self):
        if self.writable:
            os.write(self._fd, self.buffer.getvalue())
            self.buffer.truncate(0), self.buffer.seek(0)
 
 
class IOWrapper(IOBase):
    def __init__(self, file):
        self.buffer = FastIO(file)
        self.flush = self.buffer.flush
        self.writable = self.buffer.writable
        self.write = lambda s: self.buffer.write(s.encode("ascii"))
        self.read = lambda: self.buffer.read().decode("ascii")
        self.readline = lambda: self.buffer.readline().decode("ascii")
 
 
sys.stdin, sys.stdout = IOWrapper(sys.stdin), IOWrapper(sys.stdout)
input = lambda: sys.stdin.readline().rstrip("\r\n")
 
# endregion
 
if __name__ == "__main__":
    def bootstrap(f, stack=[]):
        def wrappedfunc(*args, **kwargs):
            if stack:
                return f(*args, **kwargs)
            else:
                to = f(*args, **kwargs)
                while True:
                    if type(to) is GeneratorType:
                        stack.append(to)
                        to = next(to)
                    else:
                        stack.pop()
                        if not stack:
                            break
                        to = stack[-1].send(to)
                return to
        return wrappedfunc
    
    ip = lambda: input()
    iip = lambda: int(input())
    miip = lambda: map(int, input().split())
    liip = lambda: list(map(int, input().split()))
    sip = lambda: input().split() # splitted input
    lip = lambda: list(input())
    
    main()
