def freq(l):
    d = {}
    for i in l:
        d[i] = d.get(i,0)+1
    return d

def lgcd(l):
    a = 0
    for i in l:
        a = gcd(a,i)
    return a

def SieveOfEratosthenes(num):
    prime = [True for i in range(num+1)]
    p = 2
    while (p * p <= num):
        if (prime[p] == True):
            for i in range(p * p, num+1, p):
                prime[i] = False
        p += 1
    return p[2:]

