from collections import Counter, defaultdict
from heapq import heappop, heappush
from sys import stdin, gettrace

if gettrace():
    def inputi():
        return input()
else:
    def input():
        return next(stdin)[:-1]

    def inputi():
        return stdin.buffer.readline()

INF = 1_000_000_000_000_000_000

def solve(n, m, k, xx, abcdh):
    lt = [set() for _ in range(n+1)]
    ladders = [defaultdict(list) for _ in range(n+1)]
    for a,b,c,d,h in abcdh:
        ladders[a][b].append((c,d,h))
    costs = [defaultdict(lambda: INF) for _ in range(n+1)]
    costs[1][1] = 0
    lt[1].add(1)
    ladders[n][m].append((n,m,0))
    for f in range(1, n+1):
        if not lt[f]:
            continue
        fr = sorted(lt[f].union(ladders[f].keys()))
        pc = INF
        pr = 0
        for r in fr:
            if r in lt[f]:
                pc = costs[f][r] = min(costs[f][r], pc + (r - pr)*xx[f-1])
                pr = r
            else:
                costs[f][r] = pc + (r - pr)*xx[f-1]
        pc = INF
        pr = m+1
        for r in fr[::-1]:
            if r in lt[f]:
                pc = costs[f][r] = min(costs[f][r], pc + (pr -r)*xx[f-1])
                pr = r
            else:
                costs[f][r] = min(costs[f][r], pc + (pr - r) * xx[f-1])
        for b,cdh in ladders[f].items():
            for c,d,h in cdh:
                lt[c].add(d)
                costs[c][d] = min(costs[c][d], costs[f][b] - h)
    if m not in costs[n]:
        return 'NO ESCAPE'
    else:
        return costs[n][m]



def main():
    t = int(input())
    for _ in range(t):
        n,m,k = map(int, input().split())
        xx = [int(a) for a in input().split()]
        abcdh = [[int(a) for a in input().split()] for _ in range(k)]
        print(solve(n, m, k, xx, abcdh))

if __name__ == "__main__":
    main()
