import os
import sys
from io import BytesIO, IOBase
import math 

def dfsVisited (G, node, nodeInfo, V, P):  

    V.add (node) 
    ans = 0 
    for v in G[node]: 
        if v not in V: 
            ans += dfsVisited (G, v, nodeInfo, V, P)

    ans += P[node-1] 
    nodeInfo[node-1][0] = ans 
    return ans  

def dfsCheckBecameHappy (G, node, nodeInfo, V): 

    V.add (node) 
    childUnhappy = 0 
    childHappy = 0 

    for v in G[node]: 
        if v not in V: 
            childHappy += nodeInfo[v-1][1] 

    if childHappy > nodeInfo[node-1][1]: return False
    ans = True 

    for v in G[node]: 
        if v not in V: 
            ans = ans and dfsCheckBecameHappy (G, v, nodeInfo, V) 
    return ans 

def main(): 
    t = int(input()) 
    for _ in range (t): 
        n,m = map(int, input().split()) 
        P = list(map(int, input().split())) 
        H = list(map(int, input().split())) 

        root = 1 
        graph = dict() 
        nodeInfo = [[-1]*3 for i in range (n)] #visitedPeople, happy, unhappy 

        for i in range (1, n+1): 
            graph[i] = list() 

        for _ in range (n-1): 
            u,v = map(int, input().split()) 
            graph[u].append (v) 
            graph[v].append (u) 

        visited = set() 
        _ = dfsVisited(graph, 1, nodeInfo, visited, P) 

        okay = True 
        for i in range (n): 
            happy = (nodeInfo[i][0]+H[i])/2 
            if math.floor(happy) != math.ceil(happy): 
                okay = not okay 
                break 
            unhappy = nodeInfo[i][0]-happy 
            if happy<0 or unhappy<0: 
                okay = not okay
                break
            nodeInfo[i][1] = int(happy)
            nodeInfo[i][2] = int(unhappy)

        if not okay: 
            print ("NO") 
            continue

        visited = set() 
        if dfsCheckBecameHappy (graph, 1, nodeInfo, visited): 
            print ("YES")
        else: 
            print ("NO")   

# region fastio
BUFSIZE = 8192
class FastIO(IOBase):
    newlines = 0

    def __init__(self, file):
        self._fd = file.fileno()
        self.buffer = BytesIO()
        self.writable = "x" in file.mode or "r" not in file.mode
        self.write = lambda s: self.buffer.write(s.encode()) if self.writable else None

    def read(self):
        if self.buffer.tell():
            return self.buffer.read().decode("ascii")
        return os.read(self._fd, os.fstat(self._fd).st_size).decode("ascii")

    def readline(self):
        while self.newlines == 0:
            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))
            self.newlines = b.count(b"\n") + (not b)
            ptr = self.buffer.tell()
            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)
        self.newlines -= 1
        return self.buffer.readline().decode("ascii")

    def flush(self):
        if self.writable:
            os.write(self._fd, self.buffer.getvalue())
            self.buffer.truncate(0), self.buffer.seek(0)


def print(*args, sep=" ", end="\n", file=sys.stdout, flush=False):
    at_start = True
    for x in args:
        if not at_start:
            file.write(sep)
        file.write(str(x))
        at_start = False
    file.write(end)
    if flush:
        file.flush()

sys.stdin, sys.stdout = FastIO(sys.stdin), FastIO(sys.stdout)
input = lambda: sys.stdin.readline().rstrip("\r\n")

# endregion
sys.setrecursionlimit(int(1e7)) 
if __name__ == "__main__":
    main()
