n = int(input())
tests = [input() for _ in range(n)]

for test in tests:
    a, b = [int(x) for x in test.split(' ')]

    count = 0
    while True:
        if a == b:
            print(count)
            break
        else:
            diff = a - b
            tens = abs(diff / 10)
            if tens.is_integer():
                count += tens
            else:
                count += (int(tens)+1)
            print(int(count))
            break
