from string import ascii_lowercase


s = input()
lc = {c: 0 for c in ascii_lowercase}
llc = dict()

for c in s:
    for v in lc:
        if v + c not in llc:
            llc[v + c] = lc[v]
        else:
            llc[v + c] += lc[v]
    lc[c] += 1

print(max(*lc.values(), *llc.values()))
