#!/usr/bin/env python3

import sys

class Input(object):
    def __init__(self):
        self.fh = sys.stdin

    def next_line(self):
        while True:
            line = sys.stdin.readline()
            if line == '\n':
                continue
            return line


    def next_line_ints(self):
        line = self.next_line()
        return [int(x) for x in line.split()]

    def next_line_strs(self):
        line = self.next_line()
        return line.split()

def get_max_objects(s, m, k):
    low = 0
    high = len(s)
    while low < high:
        mid = (low + high) // 2
        if can_fit(s, mid, m, k):
            high = mid
        else:
            low = mid + 1
    return len(s) - low

def can_fit(s, start, m, k):
    left = 0
    for i in range(start, len(s)):
        if left < s[i]:
            if m == 0:
                return False
            m -= 1
            left = k
        left -= s[i]
    return True

def main():
    input = Input()
    while True:
        nums = input.next_line_ints()
        if not nums:
            break
        n, m, k = nums
        s = input.next_line_ints()
        max_num = get_max_objects(s, m, k)
        print("{}".format(max_num))

main()
