T = input()

for _ in xrange(T):
    n, m = map(int, raw_input().split())
    ll = []
    for _ in xrange(n):
        t, l, h = map(int, raw_input().split())
        ll.append([t, l, h])

    check = False
    if m < ll[0][1]:
        if ll[0][0]+m < ll[0][1]:
            check = True
    elif m > ll[0][2]:
        if m-ll[0][0] > ll[0][2]:
            check = True

    if check:
        print "NO"
    else:
        mon, mox = ll[0][1], ll[0][2]
        if m < ll[0][1]:
            mox = min(ll[0][2], m+ll[0][0])
        elif m > ll[0][2]:
            mon = max(m-ll[0][0], ll[0][1])
        else:
            mon = max(m-ll[0][0], ll[0][1])
            mox = min(m+ll[0][0], ll[0][2])

        for i in xrange(1, n):
            #print mon, mox
            diff = ll[i][0] - ll[i-1][0]

            if mon > ll[i][2]:
                if mon-diff > ll[i][2]:
                    check = True
                    break
                else:
                    mon = max(ll[i][1], mon-diff)
                    mox = ll[i][2]
            elif mox < ll[i][1]:
                if mox+diff < ll[i][1]:
                    check = True
                    break
                else:
                    mon = ll[i][1]
                    mox = min(ll[i][2], mox+diff)
            else:
                mon = max(mon-diff, ll[i][1])
                mox = min(mox+diff, ll[i][2])

            #print mon, mox, ll[i]
            #print

        if check:
            print "NO"
        else:
            print "YES"
