for _ in range(int(input())):
    n = int(input())
    s = input()
    li = s.find('1')
    ri = s.rfind('1')
    m = n // 2
    if li == -1:
        print(n)
    else:
        if n % 2 != 0:
            a = max(abs(m-li), abs(m-ri)) + m + 1
        else:
            x = abs(m-1-li) if li <= m-1 else abs(m-li)
            y = abs(m-1-ri) if ri <= m-1 else abs(m-ri)
            a = max(x, y) + m + 1
        print(a * 2)
